import './InputForm.css'
import {useState} from 'react'
const InputForm = (props) =>
{
    const [currentSavings,setCurrentSavings]=useState(0);
    const [yearlyContribution,setYearlyContribution]=useState(0);
    const [expectedReturn,setExpectedReturn]=useState(0);
    const [duration,setDuration]=useState(0);
    const submitHandler= (event) =>
    {
      event.preventDefault();
      const data=
      {
        'current-savings':currentSavings,
        'yearly-contribution':yearlyContribution,
        'expected-return':expectedReturn,
        'duration':duration,
      };
      console.log(data);
      
    }
    const changeHandler=(id,val) =>
    {
        if(id==="current-savings"){
          setCurrentSavings(val);
        }
        else if(id ==="yearly-contribution"){
          setYearlyContribution(val);
        }
        else if(id==="expected-return"){
          setExpectedReturn(val);
        }
        else{
          setDuration(val);
        }
    }
    return(
        <form className="form" onSubmit={submitHandler}>
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input type="number" id="current-savings" onChange={(event)=>changeHandler("current-savings",event.target.value)} />
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution" onChange={(event)=>changeHandler("yearly-contribution",event.target.value)}/>
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number" id="expected-return" onChange={(event)=>changeHandler("expected-return",event.target.value)} />
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration" onChange={(event)=>changeHandler("duration",event.target.value)}/>
          </p>
        </div>
        <p className="actions">
          <button type="reset" className="buttonAlt">
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>
    );
}
export default InputForm;
