import Header from './components/Header'
import InputForm from './components/InputForm'
import ResultsTable from './components/ResultsTable'
import {useState} from 'react'
function App() {
  const [yearlyData,setYearlyData]=useState([]);
  const calculateHandler = (userInput) => {
    const [currentSavings,setCurrentSavings]=useState(+userInput['current-savings']);

    const yearlyContribution = +userInput['yearly-contribution']; // as mentioned: feel free to change the shape...
    const expectedReturn = +userInput['expected-return'] / 100;
    const duration = +userInput['duration'];
    // The below code calculates yearly results (total savings, interest etc)
    setYearlyData([]);

    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings = currentSavings + yearlyInterest + yearlyContribution;
      setYearlyData(
        (prev)=> [
        {
          year: i + 1,
          yearlyInterest: yearlyInterest,
          savingsEndOfYear: currentSavings,
          yearlyContribution: yearlyContribution,
        },...prev]
      );
    }
  };
  return (
    <div>
      <Header/>
      <InputForm calculateHandler={calculateHandler}/>
      <ResultsTable yearlyData={yearlyData}/>
    </div>
  );
}

export default App;
